import React, { useState } from "react";

// --- 1. THE CHILD COMPONENT (No changes here, it just receives data) ---
function OrderBook({ orders = [] }) {
  return (
    <div style={{ fontFamily: "sans-serif", padding: "20px", border: "1px solid #ddd", borderRadius: "8px" }}>
      <h3>Live Orders</h3>

      {/* Header */}
      <div style={{ display: "flex", fontWeight: "bold", borderBottom: "2px solid #333", paddingBottom: "5px" }}>
        <span style={{ width: 100 }}>Symbol</span>
        <span style={{ width: 100 }}>Price</span>
        <span style={{ width: 100 }}>Qty</span>
      </div>

      {/* The Render Loop */}
      {orders.length === 0 ? (
        <p style={{ color: "gray" }}>No orders match filter.</p>
      ) : (
        orders.map((order) => (
          <div key={order.sym} style={{ display: "flex", borderBottom: "1px solid #eee", padding: "8px 0" }}>
            <span style={{ width: 100 }}>{order.sym}</span>
            <span style={{ width: 100, color: order.price < 0 ? "red" : "green", fontWeight: "bold" }}>
              {order.price}
            </span>
            <span style={{ width: 100 }}>{order.quantity}</span>
          </div>
        ))
      )}
    </div>
  );
}

// --- 2. THE MAIN APP COMPONENT (Added Button Logic) ---
export default function App() {
  // Data State
  const [dummyOrders, setDummyOrders] = useState([
    { sym: "AAPL", price: 150.25, quantity: 100 },
    { sym: "TSLA", price: -12.50, quantity: 50 },
    { sym: "MSFT", price: 310.00, quantity: 10 },
    { sym: "GOOG", price: -5.00, quantity: 200 },
    { sym: "AMZN", price: 105.00, quantity: 20 }
  ]);

  // UI State: Are we filtering?
  const [showLargeOnly, setShowLargeOnly] = useState(false);

  // --- THE FILTER LOGIC ---
  // If showLargeOnly is true, filter the array. Otherwise, use the original array.
  const visibleOrders = showLargeOnly
    ? dummyOrders.filter((order) => order.quantity > 50)
    : dummyOrders;

  return (
    <div className="App" style={{ padding: 20 }}>

      {/* THE CONTROL PANEL */}
      <div style={{ marginBottom: 20 }}>
        <button 
          onClick={() => setShowLargeOnly(!showLargeOnly)}
          style={{
            padding: "10px 20px",
            backgroundColor: showLargeOnly ? "blue" : "gray", // Visual feedback
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer"
          }}
        >
          {showLargeOnly ? "Show All Orders" : "Show Large Orders Only (>50)"}
        </button>
      </div>

      {/* Pass the FILTERED list to the child */}
      <OrderBook orders={visibleOrders} />

    </div>
  );
}
