import React, { useState } from 'react';
import './styles.css'; 

// --- HELPER: Generate Data ---
const generateData = () => {
  const data = [];

  for (let i = 0; i < 50; i++) {
    data.push({
      sym: `STK-${i + 100}`,
      price: (Math.random() * 400 - 100).toFixed(2),
      quantity: Math.floor(Math.random() * 500)
    });
  }

  return data;
};

// --- CHILD COMPONENT ---
function OrderBook({ orders = [] }) {
  // 2. Create a "Pointer" to hold the Timer ID
  // We use useRef because we don't want to re-render the component just because the timer ID changed.
  const timerRef = useRef(null);

  // 3. The Debounce Logic
  const handleScroll = (e) => {
    // A. If there is already a timer running, KILL IT.
    // This cancels the previous "execution" because the user is still moving.
    if (timerRef.current) {
      clearTimeout(timerRef.current);
    }

    // B. Start a brand new timer.
    // If the user doesn't scroll again for 500ms, this code will finally run.
    timerRef.current = setTimeout(() => {

      // --- THE ACTUAL CODE TO RUN ---
      console.log("User has stopped scrolling!");

      // Example: Check if they hit the bottom
      const { scrollTop, scrollHeight, clientHeight } = e.target;
      if (scrollHeight - scrollTop === clientHeight) {
        console.log("Reached the bottom - Load more data?");
      }
      // -----------------------------

    }, 500); // Wait 500ms
  };

  return (
    <div className="order-book">
      <h3>Live Orders</h3>
      <div className="header-row">
        <span className="col">Symbol</span>
        <span className="col">Price</span>
        <span className="col">Qty</span>
      </div>

      {/* 4. Attach the handler to onScroll */}
      <div 
        className="scroll-container" 
        onScroll={handleScroll} 
      >
        {orders.length === 0 ? (
          <p style={{ color: "gray", padding: "10px" }}>No orders match filter.</p>
        ) : (
          orders.map((order) => (
            <div key={order.sym} className="order-row">
              <span className="col">{order.sym}</span>
              <span className={`col ${order.price < 0 ? "text-red" : "text-green"}`}>
                {order.price}
              </span>
              <span className="col">{order.quantity}</span>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

// --- MAIN COMPONENT ---
export default function App() {
  const [dummyOrders] = useState(generateData());
  const [showLargeOnly, setShowLargeOnly] = useState(false);

  const visibleOrders = showLargeOnly
    ? dummyOrders.filter((order) => order.quantity > 250)
    : dummyOrders;

  return (
    <div className="App">
      <div style={{ marginBottom: 20 }}>
        <button 
          onClick={() => setShowLargeOnly(!showLargeOnly)}
          className={showLargeOnly ? "btn-active" : "btn-inactive"}
        >
          {showLargeOnly ? "Show All Orders" : "Show Large Orders Only (>250)"}
        </button>
      </div>

      <OrderBook orders={visibleOrders} />
    </div>
  );
}
