import React, { useState } from "react";

// --- 1. THE CHILD COMPONENT (Displays the list) ---
function OrderBook({ orders = [] }) {
  return (
    <div style={{ fontFamily: "sans-serif", padding: "20px" }}>
      <h3>Live Orders</h3>

      {/* Header Row */}
      <div style={{ display: "flex", fontWeight: "bold", borderBottom: "2px solid #333", paddingBottom: "5px" }}>
        <span style={{ width: 100 }}>Symbol</span>
        <span style={{ width: 100 }}>Price</span>
        <span style={{ width: 100 }}>Qty</span>
      </div>

      {/* The Loop: If orders is empty, this just renders nothing instead of crashing */}
      {orders.map((order) => (
        <div key={order.sym} style={{ display: "flex", borderBottom: "1px solid #eee", padding: "8px 0" }}>

          <span style={{ width: 100 }}>{order.sym}</span>

          {/* Color Logic: Red if negative, Green if positive */}
          <span style={{ width: 100, color: order.price < 0 ? "red" : "green", fontWeight: "bold" }}>
            {order.price}
          </span>

          <span style={{ width: 100 }}>{order.quantity}</span>
        </div>
      ))}
    </div>
  );
}

// --- 2. THE MAIN APP COMPONENT (The Entry Point) ---
export default function App() {
  // Define the data here
  const [dummyOrders, setDummyOrders] = useState([
    { sym: "AAPL", price: 150.25, quantity: 100 },
    { sym: "TSLA", price: -12.50, quantity: 50 },
    { sym: "MSFT", price: 310.00, quantity: 10 },
    { sym: "GOOG", price: -5.00, quantity: 200 }
  ]);

  return (
    <div className="App">
      {/* Pass the data DOWN to the child */}
      <OrderBook orders={dummyOrders} />
    </div>
  );
}
