import React, { useState } from "react";

// --- TODO: IMPLEMENT THIS COMPONENT ---
function TradeForm({ onAddTrade }) {
  // Hint: You will need state variables for 'symbol' and 'price'

  const [symbol, setSymbol] = useState("");
  const [price, setPrice] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault(); // Good practice for forms

    // TODO: 
    // 1. Create a trade object { id: Date.now(), symbol: ..., price: ... }
    const newTrade = {
      id: Date.now(),
      symbol: symbol.toUpperCase(),
      price: parseFloat(price),
    }

    onAddTrade(newTrade);
    // 3. Reset the input fields to empty

    setSymbol = "";
    setPrice = "";
  };

  return (
    <div style={{ borderBottom: "2px solid #ccc", paddingBottom: "20px", marginBottom: "20px" }}>
      <h3>New Order Ticket</h3>
      <form onSubmit={handleSubmit} style={{ display: "flex", gap: "10px" }}>

        <input
          type="text" 
          placeholder="Symbol (eg. AAPL)"
          value={symbol}
          onChange={(e) => setSymbol(e.target.value)}
        />

        <input 
          type="text" 
          placeholder="Price (eg. 10.5)"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
        />

        <button type="submit">Execute</button>
      </form>
    </div>
  );
}

// --- EXISTING LIST COMPONENT (Already done) ---
function TradeList({ trades }) {
  return (
    <div>
      {trades.map((t) => (
        <div key={t.id} style={{ borderBottom: "1px solid #eee", padding: "5px" }}>
          <strong>{t.symbol}</strong>: <span style={{ color: t.price >= 0 ? "green" : "red" }}>{t.price}</span>
        </div>
      ))}
    </div>
  );
}

// --- MAIN APP ---
export default function App() {
  const [trades, setTrades] = useState([
    { id: 1, symbol: "AAPL", price: 150.00 },
    { id: 2, symbol: "TSLA", price: -10.50 }
  ]);

  const handleAddTrade = (newTrade) => {
    // This adds the new trade to the TOP of the array
    setTrades([newTrade, ...trades]);
  };

  return (
    <div style={{ padding: 20, fontFamily: "sans-serif" }}>
      {/* Pass the handler down to the form */}
      <TradeForm onAddTrade={handleAddTrade} />

      <TradeList trades={trades} />
    </div>
  );
}
